import React, { Component } from "react";

import { 
  MDBContainer,
  MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, 
  MDBCollapse, 
  MDBFormInline, 
  MDBTabContent, MDBTabPane,
} from "mdbreact";

import Competence from "./competence";
import Formation from "./formation";
import Contact from "./contact";
import Experience from "./experience";

import picture from "../pictures/IMG_6767.JPG";
import "../index.css";

class NavbarPage extends Component {
state = {
    activeItem: "1"
}

toggle = tab => () => {
    if (this.state.activeItem !== tab) {
    this.setState({
    activeItem: tab
    });
    }
}

render() {
  return (
    <>
      <div className="container-area"></div>
      <header>
        <MDBNavbar dark expand="md">

          <MDBNavbarBrand>
              <img id="pic" src={picture} />
              <strong id="haut" className="white-text">MonCV</strong>
          </MDBNavbarBrand>

          <MDBNavbarToggler onClick={this.toggleCollapse} />

          <MDBCollapse id="navbarCollapse3" isOpen={this.state.isOpen} navbar>

            <MDBNavbarNav left>
              <MDBNavItem>
                <MDBNavLink link to="#"
                active={this.state.activeItem === "1"}
                onClick={this.toggle("1")}
                role="tab">Contact</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                  <MDBNavLink link to="#!"
                  active={this.state.activeItem === "2"}
                  onClick={this.toggle("2")}
                  role="tab">Compétences</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                  <MDBNavLink to="#!">Expériences</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                  <MDBNavLink to="#!">Formation</MDBNavLink>
              </MDBNavItem>
            </MDBNavbarNav>

            <MDBNavbarNav right>
              <MDBNavItem>
                <MDBFormInline waves>
                  <div className="md-form my-0">
                    <input className="form-control mr-sm-2" type="text" placeholder="Search" aria-label="Search" />
                  </div>
                </MDBFormInline>
              </MDBNavItem>
            </MDBNavbarNav>
          </MDBCollapse>
        </MDBNavbar>
      </header>

      <main>
        <MDBTabContent className="card" activeItem={this.state.activeItem}>
            <MDBTabPane tabId="1" role="tabpanel">
              <Contact />
            </MDBTabPane>
            <MDBTabPane tabId="2" role="tabpanel">
              <Competence />
            </MDBTabPane>
            <MDBTabPane tabId="3" role="tabpanel">
              <Experience />
            </MDBTabPane>
            <MDBTabPane tabId="4" role="tabpanel">
              <Formation />
            </MDBTabPane>
        </MDBTabContent>
      </main>

    </>
    );
  }
}

export default NavbarPage;
