import React, { Component } from "react";

import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, 
    MDBCollapse, 
    MDBTabPane, MDBTabContent, 
    MDBIcon,
} from "mdbreact";

import { BrowserRouter as Router, Link } from 'react-router-dom';

import picture from "../pictures/IMG_6767.JPG";
import "../index.css";

class NavbarPage extends Component {
  state = {
    collapseID: ""
  };
  state = {
    items: {
        default: "1",
    }
}


toggleCollapse = () => {
  this.setState({ isOpen: !this.state.isOpen });
}

togglePills = (type, tab) => e => {
  e.preventDefault();
  if (this.state.items[type] !== tab) {
    let items = { ...this.state.items };
    items[type] = tab;
    this.setState({
      items
    });
  }
};

render() {
  return (
    <>
        <div className="container-area">
            <div className="area">
                <ul className="circles">
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                </ul>
            </div>
        </div>

            <header>
                <MDBNavbar color="indigo darken-4" dark expand="md" fixed="top">
                    
                    <MDBNavbarBrand>
                        <img id="pic" src={picture} />
                        <strong id="haut" className="white-text">MonCV</strong>
                    </MDBNavbarBrand>
                   
                    <MDBNavbarToggler onClick={this.toggleCollapse("navbarCollapse")} />
                    
                    <MDBCollapse
                        id="navbarCollapse"
                        isOpen={this.state.collapseID}
                        navbar
                    >

                    <MDBNavbarNav left>
                        <MDBNavItem>
                            <MDBNavLink active={this.state.items["default"] === "2"} onClick={this.togglePills("default", "2")}>Compétences</MDBNavLink>
                        </MDBNavItem>
                        <MDBNavItem>
                            <MDBNavLink active={this.state.items["default"] === "3"} onClick={this.togglePills("default", "3")}>Expérience</MDBNavLink>
                        </MDBNavItem>
                        <MDBNavItem>
                            <MDBNavLink active={this.state.items["default"] === "4"} onClick={this.togglePills("default", "4")}>Formation</MDBNavLink>
                        </MDBNavItem>
                    </MDBNavbarNav>

                    <MDBNavbarNav right>
                        <MDBNavItem  active>
                            <MDBNavLink to="#!" active={this.state.items["default"] === "1"} onClick={this.togglePills("default", "1")}>
                                <MDBIcon className="d-none d-md-inline" icon='home' size='2x' />
                            </MDBNavLink>
                        </MDBNavItem>
                            
                        </MDBNavbarNav>
                    </MDBCollapse>
                </MDBNavbar>
            </header>

        
        <main>
        <MDBTabContent id="fd" activeItem={this.state.items["default"]}>
            <MDBTabPane tabId="1">
                <Link to={'/contact'}></Link>
            </MDBTabPane>
            <MDBTabPane tabId="2">
                <Link to={'/competence'}></Link>
            </MDBTabPane>
            <MDBTabPane tabId="3">
                <Link to={'/experience'}></Link>
            </MDBTabPane>
            <MDBTabPane tabId="4">
                <Link to={'/formation'}></Link>
            </MDBTabPane>

        </MDBTabContent>
        </main>
    </>
    
    );
  }
}

export default NavbarPage;
